import React, { Fragment, useState } from "react";
import mealsImg from "../../assets/meals.jpg";
import classes from "./Header.module.css";
import HeaderCardButton from "./HeaderCartButton";

const Header = (props) => {

  const [opacity, setOpacity] = useState(0.7);

  window.onscroll = () => {
    let newScrollHeight = (window.scrollY + .75 )/ 100;


    if (newScrollHeight === 0) newScrollHeight = 0.75;
    console.log(newScrollHeight);

    setOpacity(newScrollHeight);
};

  return (
    <Fragment>
      <header className={classes.header} style={{ background: `rgba(122, 0, 0, ${opacity})` }} >
        <h1>Meal Orders</h1>
        <HeaderCardButton />
      </header>
      <div className={classes["main-image"]}>
        <img src={mealsImg} alt="food table header" />
      </div>
    </Fragment>
  );
};

export default Header;

