import './NewExpense.css';
import ExpenseForm from './ExpenseForm';
import React, {useState} from 'react';

const NewExpense = (props) => {

    const showExpenseFormStr = <ExpenseForm onSaveExpenseData={onSaveExpenseDataHandler} onCancel={hideExpenseForm}/>;

    const showExpenseForm = () => {
        changeExpenseForm();
    } 

    const hideExpenseForm = () => {
        return changeExpenseForm(<button onClick={showExpenseForm}>Add New Expense</button>);
    }

    const [expenseForm, changeExpenseForm] = useState(<button onClick={showExpenseForm}>Add New Expense</button>);

    const onSaveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData, 
            id: Math.random().toString(),
        };
        props.onAddExpense(expenseData);
    }

    return (
        <div className='new-expense'>
            {expenseForm}
        </div>
    );
};


export default NewExpense;
